import React, { Component } from "react";
import "./Produto.scss";
import Grid from '@mui/material/Grid';
import FormProduto from "./../../components/forms/FormProduto"
import Menu from "./../../components/Menu"

const initialState = {
  produto: {
    id: '',
    nome: '',
    subGrupo: '',
    grupo: '',
    valor: ''
  },
  menuOpen:false
}

export default class Cadastro extends Component {
  state = { ...initialState }

  UNSAFE_componentWillMount() {
		this.setState({
			produto: {
        id: '',
        nome: '',
        subGrupo: '',
        grupo: '',
        valor: ''
      }
		})
    const{match:{params}}= this.props
    if(params.produto_id){
      let list_produto = JSON.parse(localStorage.getItem('list_produto'))
		  let index = list_produto.findIndex(param => param.id=== parseInt(params.produto_id))
      if(index=== -1){
        alert('Produto nao encontrado')
        this.props.history.push("/")
        return false;
      }
    this.setState({produto:{...list_produto[index]}})
    }
	}

  updateProduto(event) {
    const { name, value } = event.target
    let { produto } = this.state

    produto[name] = value
    this.setState({ produto })
  }

  validarCampos(produto){
    if(produto.nome===""||produto.subGrupo===""||produto.grupo===""||produto.valor===""){
      return false;
    }
    return true
  }

  salvarProduto(){
    let{produto} = this.state
    if(!this.validarCampos(produto)){
      alert('Preencher todos os campos')
      return false
    }
    let list_produto = JSON.parse(localStorage.getItem('list_produto'))
    if(produto.id!==""){
      let index = list_produto.findIndex(param => param.id===produto.id)
      list_produto[index]={...produto}
    }else{

      let id = ""
      if(!list_produto){
        list_produto=[]
        id = 1
      }else{
        id = list_produto[list_produto.length-1].id+1
      }
      list_produto.push({...produto, id})
    }
    localStorage.setItem("list_produto", JSON.stringify(list_produto))
    this.setState({produto: {
      id: '',
      nome: '',
      subGrupo: '',
      grupo: '',
      valor: ''
    }})
    alert("Produto cadastrado com sucesso")
    this.props.history.push("/")
  }

  abrirFecharMenu(){
		this.setState({
			menuOpen:!this.state.menuOpen
		})
	}

  render() {
    console.log(this.state.produto)
    return (
      <div style={{ width: "100%", padding: 20 }}>
        <Menu tela="Cadastro de Produtos" menuOpen={this.state.menuOpen} abrirFecharMenu={() => this.abrirFecharMenu()}/>
        <Grid container spacing={1} direction="row">
          <Grid item md={12} sm={6} xs={1}>
          </Grid>
        </Grid>
        <FormProduto dados={this.state.produto} updateField={(e) => this.updateProduto(e)} salvarProduto={()=> this.salvarProduto()} />
      </div>
    )
  }
}
