import React, { useState, useEffect } from "react";
import "./Produto.scss";
import Grid from '@mui/material/Grid';
import FormProduto from "./../../components/forms/FormProduto"
import Menu from "./../../components/Menu"
import { useNavigate, useParams } from "react-router-dom"

const initialState = {
  produto: {
    id: '',
    nome: '',
    subGrupo: '',
    grupo: '',
    valor: ''
  },
  menuOpen: false
}

const CadastroProduto = () => {
  const [state, setState] = useState({ ...initialState })
  const navigate = useNavigate()
  const { produto_id } = useParams()

  const updateProduto = (event) => {
    const { name, value } = event.target
    let { produto } = state

    produto[name] = value
    setState((prevState) => ({ ...prevState, produto }))
  }

  const validarCampos = (produto) => {
    if (produto.nome === "" || produto.subGrupo === "" || produto.grupo === "" || produto.valor === "") {
      return false;
    }
    return true
  }

  const salvarProduto = async () => {
    try {
      let { produto } = state
      if (!validarCampos(produto)) {
        alert('Preencher todos os campos')
        return false
      }
      setState((prevState) => ({
        ...prevState,
        produto: {
          id: '',
          nome: '',
          subGrupo: '',
          grupo: '',
          valor: ''
        }
      }))
      alert("Produto cadastrado com sucesso")
      navigate("/")

    } catch (error) {
      console.log(error)
    }


    let { produto } = state
    if (!validarCampos(produto)) {
      alert('Preencher todos os campos')
      return false
    }

    setState((prevState) => ({
      ...prevState,
      produto: {
        id: '',
        nome: '',
        subGrupo: '',
        grupo: '',
        valor: ''
      }
    }))
    alert("Produto cadastrado com sucesso")
    navigate("/")
  }

  const abrirFecharMenu = () => {
    setState((prevState) => ({
      ...prevState,
      menuOpen: !state.menuOpen
    }))
  }

  useEffect(() => {
    setState((prevState) => ({
      ...prevState,
      produto: {
        id: '',
        nome: '',
        subGrupo: '',
        grupo: '',
        valor: ''
      }
    }))
    if (produto_id) {
      let list_produto = JSON.parse(localStorage.getItem('list_produto'))
      let index = list_produto.findIndex(param => param.id === parseInt(produto_id))
      if (index === -1) {
        alert('Produto nao encontrado')
        navigate("/")
        return false;
      }
      setState((prevState) => ({ ...prevState, produto: { ...list_produto[index] } }))
    }
  }, [navigate, produto_id])

  return (
    <div style={{ width: "100%", padding: 20 }}>
      <Menu tela="Cadastro de Produtos" menuOpen={state.menuOpen} abrirFecharMenu={() => abrirFecharMenu()} />
      <Grid container spacing={1} direction="row">
        <Grid item md={12} sm={6} xs={1}>
        </Grid>
      </Grid>
      <FormProduto dados={state.produto} updateField={(e) => updateProduto(e)} salvarProduto={() => salvarProduto()} />
    </div>
  )
}

export default CadastroProduto
