import React, { Component } from "react";
import "./Produto.scss";
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom'
import Tabela  from "./../../components/Tabela"
import Menu from ".//../../components/Menu"
// Define as variaveis iniciais do estado da pagina
const initialState = {
	acoesTabela: ["edit", "remove"],
	list_produto: [],
	cabecalho: [
		{
			id: 'nome',
			label: 'Nome',
			numeric: false,
		},
		{
			id: 'subGrupo',
			label: 'Sub Grupo',
			numeric: false,
		},
		{
			id: 'grupo',
			label: 'Grupo',
			numeric: false,
		},
		{
			id: 'valor',
			label: 'Valor',
			numeric: false,
		}
	],
	menuOpen:false
}

export default class ListagemProduto extends Component {
	state = { ...initialState } //define o estado inicial da aplicacao


	// Funcao que executa quando a tela eh carregada
	UNSAFE_componentWillMount() {
		let list_produto = JSON.parse(localStorage.getItem('list_produto')) 
		this.setState({
			list_produto: list_produto || []
		})
	}

	removerProduto(id) {
		let list_produto = JSON.parse(localStorage.getItem('list_produto'))
		let index = list_produto.findIndex(param => param.id===id)
		list_produto.splice(index,1)
		this.setState({list_produto})
		localStorage.setItem('list_produto', JSON.stringify(list_produto))
		if(list_produto.length===0){
			localStorage.removeItem('list_produto')
		}
		alert('Produto removido com sucesso')
	}

	abrirFecharMenu(){
		this.setState({
			menuOpen:!this.state.menuOpen
		})
	}

	//Uma funcao nativa do react que retorna os componentes para ser renderizada a tela
	render() {
		let {list_produto, cabecalho, acoesTabela,menuOpen }=this.state

		return (
			<React.Fragment>
				<div style={{ width: "100%", padding: 20 }}>
					<Menu tela="Listagem de Produtos" menuOpen={menuOpen} abrirFecharMenu={() => this.abrirFecharMenu()}/>
					<Grid container spacing={1} direction="row">
						<Grid item md={10} sm={6} xs={1}>
						</Grid>

						<Grid item md={2} sm={6} xs={1}>
							<Link to='/cadastro'>
								<Button variant="contained" color="primary" fullWidth>
									Cadastrar Produto
								</Button>
							</Link>
						</Grid>
					</Grid>

					<Grid container direction="row" spacing={1}>
						<Grid item md={12}>
							<Tabela list={list_produto} cabecalho={cabecalho} acoes={acoesTabela} remover={(id) => this.removerProduto(id)} caminhoEditar={'/cadastro/'}/>
						</Grid>
					</Grid>
				</div>
			</React.Fragment>

		)
	}
}
