import React, {Component} from "react";
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom'
import Tabela  from "./../../components/Tabela"
import Menu from ".//../../components/Menu"

const initialState = {
  acoesTabela:["edit", "remove"],
  list_pessoa:[],
  cabecalho:[
    {
    id:'nome',
    label:'Nome',
    numeric:false,
    },
    {
      id:'idade',
      label:'Idade',
      numeric:false,
    },
    {
      id:'numero',
      label:'Numero',
      numeric:false,
    },
    {
      id:'cpf',
      label:'CPF',
      numeric:false,
    }
  ],
  menuOpen:false
}

export default class ListagemPessoa extends Component{
state = { ...initialState}


UNSAFE_componentWillMount(){
  let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
  this.setState({
    list_pessoa: list_pessoa || []
  })
}

removerPessoa(id) {
  let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
  let index = list_pessoa.findIndex(param => param.id===id)
  list_pessoa.splice(index,1)
  this.setState({list_pessoa})
  localStorage.setItem('list_pessoa', JSON.stringify(list_pessoa))
  if(list_pessoa.length===0){
    localStorage.removeItem('list_pessoa')
  }
  alert('Pessoa removida com sucesso')
}

abrirFecharMenu(){
  this.setState({
    menuOpen:!this.state.menuOpen
  })
}

render(){
  let {list_pessoa,cabecalho, acoesTabela,menuOpen}=this.state

  return(
    <React.Fragment>
				<div style={{ width: "100%", padding: 20 }}>
					<Menu tela="Listagem de Pessoas" menuOpen={menuOpen} abrirFecharMenu={() => this.abrirFecharMenu()}/>
					<Grid container spacing={1} direction="row">
						<Grid item md={10} sm={6} xs={1}>
						</Grid>

						<Grid item md={2} sm={6} xs={1}>
							<Link to='/cadastroPessoa'>
								<Button variant="contained" color="primary" fullWidth>
									Cadastrar Pessoa
								</Button>
							</Link>
						</Grid>
					</Grid>

					<Grid container direction="row" spacing={1}>
						<Grid item md={12}>
							<Tabela list={list_pessoa} cabecalho={cabecalho} acoes={acoesTabela} remover={(id) => this.removerPessoa(id)} caminhoEditar={'/cadastroPessoa/'} />
						</Grid>
					</Grid>
				</div>
			</React.Fragment>
  )
}

}
