import React, { useState, useEffect } from "react";
import Grid from '@mui/material/Grid';
import Menu from "./../../components/Menu"
import FormPessoa from "../../components/forms/FormPessoa";
import {useNavigate, useParams} from  "react-router-dom"
import axios from "axios";

const initialState = {
  pessoa: {
    id: '',
    nome: '',
    idade: '',
    numero: '',
    cpf: ''
  },
  menuOpen: false
}

const CadastroPessoa = () => {
  const [state, setState] = useState({ ...initialState })
  const navigate = useNavigate()
  const {pessoa_id} = useParams()

  const updatePessoa = (event) => {
    const { name, value } = event.target
    let { pessoa } = state

    pessoa[name] = value
    setState((prevState) => ({ ...prevState, pessoa }))
  }

  const validarCampos = (pessoa) => {
    if (pessoa.nome === "" || pessoa.idade === "" || pessoa.numero === "" || pessoa.cpf === "") {
      return false;
    }
    return true;
  }

  const getConfig = () => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    }

    return config
  }

  const salvarPessoa = async () => {
    try {
      let { pessoa } = state
      if (!validarCampos(pessoa)) {
        alert('Preencher todos os campos')
        return false
      }
      
      pessoa.idade = parseInt(pessoa.idade)
      delete pessoa.id

      await axios.post("http://localhost:3333/pessoa", pessoa, getConfig())

      setState((prevState) => ({
        ...prevState,
        pessoa: {
          id: '',
          nome: '',
          idade: '',
          numero: '',
          cpf: ''
        }
      }))
      alert("Pessoa cadastrada com sucesso")
      navigate("/listagemPessoa")

    } catch (error) {
      console.log(error)
      alert("erro ao cadastrar")
    }
  }

  const abrirFecharMenu = () => {
    setState((prevState) => ({
      ...prevState,
      menuOpen: !this.state.menuOpen
    }))
  }

  useEffect(()=> {
    setState((prevState) => ({
      ...prevState,
      pessoa: {
        id: '',
        nome: '',
        idade: '',
        numero: '',
        cpf: ''
      }
    }))
    
    if (pessoa_id) {      
      let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
      let index = list_pessoa.findIndex(param => param.id === parseInt(pessoa_id))
      if (index === -1) {
        alert('Pessoa nao encontrada')
        navigate("/")
        return false;
      }
      setState((prevState) => ({ ...prevState, pessoa: { ...list_pessoa[index] } }))
    }
  },[navigate, pessoa_id])

  return (
    <div style={{ width: "100%", padding: 20 }}>
      <Menu tela="Cadastro de Pessoas" menuOpen={state.menuOpen} abrirFecharMenu={() => abrirFecharMenu()}/>
      <Grid container spacing={1} direction="row">
        <Grid item md={12} sm={6} xs={1}>
        </Grid>
      </Grid>
      <FormPessoa dados={state.pessoa} updateField={(e) => updatePessoa(e)} salvarPessoa={()=> salvarPessoa()} />
    </div>
  )
}

export default CadastroPessoa
