import React, { useState, useEffect } from "react";
import Grid from '@mui/material/Grid';
import Menu from "./../../components/Menu"
import FormPessoa from "../../components/forms/FormPessoa";
import {useNavigate, useParams} from  "react-router-dom"

const initialState = {
  pessoa: {
    id: '',
    nome: '',
    idade: '',
    numero: '',
    cpf: ''
  },
  menuOpen: false
}

const CadastroPessoa = () => {
  const [state, setState] = useState({ ...initialState })
  const navigate = useNavigate()
  const {pessoa_id} = useParams()

  useEffect(()=> {
    setState((prevState) => ({
      ...prevState,
      pessoa: {
        id: '',
        nome: '',
        idade: '',
        numero: '',
        cpf: ''
      }
    }))
    
    if (pessoa_id) {      
      let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
      let index = list_pessoa.findIndex(param => param.id === parseInt(pessoa_id))
      if (index === -1) {
        alert('Pessoa nao encontrada')
        navigate("/")
        return false;
      }
      setState((prevState) => ({ ...prevState, pessoa: { ...list_pessoa[index] } }))
    }
  },[])

}

export default class Cadastro extends Component {
  state = { ...initialState }


  updatePessoa(event) {
    const { name, value } = event.target
    let { pessoa } = this.state

    pessoa[name] = value
    this.setState({ pessoa })
  }

  validarCampos(pessoa) {
    if (pessoa.nome === "" || pessoa.idade === "" || pessoa.numero === "" || pessoa.cpf === "") {
      return false;
    }
    return true;
  }

  salvarPessoa() {
    let { pessoa } = this.state
    if (!this.validarCampos(pessoa)) {
      alert('Preencher todos os campos')
      return false
    }
    let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
    if (pessoa.id!=="") {
      let index = list_pessoa.findIndex(param => param.id === pessoa.id)
      list_pessoa[index] = { ...pessoa }
    } else {

      let id = ""
      if (!list_pessoa) {
        list_pessoa = []
        id = 1
      } else {
        id = list_pessoa[list_pessoa.length - 1].id + 1
      }
      list_pessoa.push({ ...pessoa, id })
    }
    localStorage.setItem("list_pessoa", JSON.stringify(list_pessoa))
    this.setState({
      pessoa: {
        id: '',
        nome: '',
        idade: '',
        numero: '',
        cpf: ''
      }
    })
    alert("Pessoa cadastrada com sucesso")
    this.props.history.push("/listagemPessoa")
  }

  abrirFecharMenu() {
    this.setState({
      menuOpen: !this.state.menuOpen
    })
  }

  render(){
    return (
      <div style={{ width: "100%", padding: 20 }}>
        <Menu tela="Cadastro de Pessoas" menuOpen={this.state.menuOpen} abrirFecharMenu={() => this.abrirFecharMenu()}/>
        <Grid container spacing={1} direction="row">
          <Grid item md={12} sm={6} xs={1}>
          </Grid>
        </Grid>
        <FormPessoa dados={this.state.pessoa} updateField={(e) => this.updatePessoa(e)} salvarPessoa={()=> this.salvarPessoa()} />
      </div>
    )
  }


}
