import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Produto.scss";
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom'
import Tabela from "./../../components/Tabela"
import Menu from ".//../../components/Menu"
import { initial } from "lodash";
// Define as variaveis iniciais do estado da pagina
const initialState = {
	acoesTabela: ["edit", "remove"],
	list_produto: [],
	cabecalho: [
		{
			id: 'nome',
			label: 'Nome',
			numeric: false,
		},
		{
			id: 'subGrupo',
			label: 'Sub Grupo',
			numeric: false,
		},
		{
			id: 'grupo',
			label: 'Grupo',
			numeric: false,
		},
		{
			id: 'valor',
			label: 'Valor',
			numeric: false,
		}
	],
	menuOpen: false
}

const ListagemProduto = () => {
	const [state, setState] = useState({ ...initialState })
	const navigate = useNavigate()

	const removerProduto = (id) => {
		let list_produto = JSON.parse(localStorage.getItem('list_produto'))
		let index = list_produto.findIndex(param => param.id === id)
		list_produto.splice(index, 1)
		setState((prevState) => ({
			...prevState,
			list_produto: list_produto
		}))
		localStorage.setItem('list_produto', JSON.stringify(list_produto))
		if (list_produto.length === 0) {
			localStorage.removeItem('list_produto')
		}
		alert('Produto removido com sucesso')
	}

	const abrirFecharMenu = () => {
		setState((prevState) => ({
			...prevState,
			menuOpen: !this.state.menuOpen
		}))

	}

	// Funcao que executa quando a tela eh carregada
	useEffect(() => {
		let list_produto = JSON.parse(localStorage.getItem('list_produto'))
		setState((prevState) => ({
			...prevState, list_produto: list_produto || []
		}))
	}, [])

	let { list_produto, cabecalho, acoesTabela, menuOpen } = state
	return (
		<React.Fragment>
			<div style={{ width: "100%", padding: 20 }}>
				<Menu tela="Listagem de Produtos" menuOpen={menuOpen} abrirFecharMenu={() => abrirFecharMenu()} />
				<Grid container spacing={1} direction="row">
					<Grid item md={10} sm={6} xs={1}>
					</Grid>

					<Grid item md={2} sm={6} xs={1}>
						<Link to='/cadastro'>
							<Button variant="contained" color="primary" fullWidth>
								Cadastrar Produto
							</Button>
						</Link>
					</Grid>
				</Grid>

				<Grid container direction="row" spacing={1}>
					<Grid item md={12}>
						<Tabela list={list_produto} cabecalho={cabecalho} acoes={acoesTabela} remover={(id) => removerProduto(id)} caminhoEditar={'/cadastro/'} />
					</Grid>
				</Grid>
			</div>
		</React.Fragment>

	)
}

export default ListagemProduto
