import React, { useState, useEffect } from "react";
import Grid from '@mui/material/Grid';
import Menu from "./../../components/Menu"
import FormPessoa from "../../components/forms/FormPessoa";
import {useNavigate, useParams} from  "react-router-dom"
import axios from "axios";

const initialState = {
  pessoa: {
    id: '',
    nome: '',
    idade: '',
    numero: '',
    cpf: ''
  },
  menuOpen: false
}

const CadastroPessoa = () => {
  const [state, setState] = useState({ ...initialState })
  const navigate = useNavigate()
  const {pessoa_id} = useParams()

  const updatePessoa = (event) => {
    const { name, value } = event.target
    let { pessoa } = state

    pessoa[name] = value
    setState((prevState) => ({ ...prevState, pessoa }))
  }

  const validarCampos = (pessoa) => {
    if (pessoa.nome === "" || pessoa.idade === "" || pessoa.numero === "" || pessoa.cpf === "") {
      return false;
    }
    return true;
  }

  const salvarPessoa = () => {
    let { pessoa } = state
    if (!validarCampos(pessoa)) {
      alert('Preencher todos os campos')
      return false
    }
    let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
    if (pessoa.id!=="") {
      let index = list_pessoa.findIndex(param => param.id === pessoa.id)
      list_pessoa[index] = { ...pessoa }
    } else {

      let id = ""
      if (!list_pessoa) {
        list_pessoa = []
        id = 1
      } else {
        id = list_pessoa[list_pessoa.length - 1].id + 1
      }
      list_pessoa.push({ ...pessoa, id })
    }
    localStorage.setItem("list_pessoa", JSON.stringify(list_pessoa))
    setState((prevState) => ({
      ...prevState,
      pessoa: {
        id: '',
        nome: '',
        idade: '',
        numero: '',
        cpf: ''
      }
    }))
    alert("Pessoa cadastrada com sucesso")
    navigate("/listagemPessoa")
  }

  const abrirFecharMenu = () => {
    setState((prevState) => ({
      ...prevState,
      menuOpen: !this.state.menuOpen
    }))
  }

  useEffect(()=> {
    setState((prevState) => ({
      ...prevState,
      pessoa: {
        id: '',
        nome: '',
        idade: '',
        numero: '',
        cpf: ''
      }
    }))
    
    if (pessoa_id) {      
      let list_pessoa = JSON.parse(localStorage.getItem('list_pessoa'))
      let index = list_pessoa.findIndex(param => param.id === parseInt(pessoa_id))
      if (index === -1) {
        alert('Pessoa nao encontrada')
        navigate("/")
        return false;
      }
      setState((prevState) => ({ ...prevState, pessoa: { ...list_pessoa[index] } }))
    }
  },[navigate, pessoa_id])

  return (
    <div style={{ width: "100%", padding: 20 }}>
      <Menu tela="Cadastro de Pessoas" menuOpen={state.menuOpen} abrirFecharMenu={() => abrirFecharMenu()}/>
      <Grid container spacing={1} direction="row">
        <Grid item md={12} sm={6} xs={1}>
        </Grid>
      </Grid>
      <FormPessoa dados={state.pessoa} updateField={(e) => updatePessoa(e)} salvarPessoa={()=> salvarPessoa()} />
    </div>
  )
}

export default CadastroPessoa
